var fs = require("fs");
function deal(path){
	var arr = fs.readdirSync(path);
	for(var i = 0 ; i < arr.length ; i ++){
		var sta = fs.statSync(path + "/" + arr[i]);
		if(sta.isDirectory()){
			deal(path + "/" + arr[i]);
		}else{
			fs.unlinkSync(path + "/" + arr[i]);
		}
	}
	fs.rmdirSync(path);
}
module.exports.deal = deal;